import { Component } from '@angular/core';
import { Cliente } from '../cliente/cliente.model';
import { ClienteService } from '../cliente.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  msg: string = "";
  obj: Cliente = new Cliente();

  constructor(
    private clienteService: ClienteService,
    private routes: ActivatedRoute
  ){}
    
  cadastrar(): void{
    try {
      this.clienteService.inserir(this.obj);
      this.msg = "Cadastro efetuado com sucesso"
    } catch (error) {
      this.msg = "Ops, parece que algo deu errado";
    }
    (document.getElementById('divCadastro') as HTMLElement).style.display = 'none';
    window.alert(this.msg);

  }
  teste(): void{
    window.alert("Nome: " + this.obj.nome + "\nEmail: ");
  }

  recuperar(): void{
    (document.getElementById('divEsqueciSenha') as HTMLElement).style.display = 'none';
    window.alert('Um link para recuperação de senha foi enviado ao seu email');

  }
}
<section class="w3-container w3-center w3-margin">
    <h2 class="w3-text-indigo">Identificação</h2>
    <div id="container">
        <!-- Possui cadastro -->
        <form class="w3-half w3-border-right w3-padding">
            <h3 class="w3-text-indigo">Já tenho cadastro</h3>
            <div class="cadastro">
                <input type="email" class="w3-input w3-border w3-round w3-margin" placeholder="Email" required pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$">
                <input type="password" class="w3-input w3-border w3-round w3-margin" placeholder="Senha" required>
            </div>
            <p><button class="w3-button w3-round w3-indigo w3-hover-orange" routerLink="/produtos">Entrar</button></p>
            <p><button class="w3-button w3-hover-none w3-text-indigo w3-hover-text-orange" onclick="document.getElementById('divEsqueciSenha').style.display='block'">Esqueci minha senha</button></p>
        </form>
    
        <!-- Não possui cadastro -->
        <div class="w3-half">
            <h3 class="w3-text-indigo">Ainda não tenho cadastro</h3>
            <button class="w3-button w3-round w3-indigo w3-hover-orange" onclick="document.getElementById('divCadastro').style.display='block'">Cadastrar</button>
        </div>
    </div>

    <!-- Modal Cadastro -->
    <div action="" class="w3-modal" id="divCadastro">
        <div class="w3-modal-content w3-card-4 w3-animate-top w3-round">
            <header class="w3-indigo w3-container modal-header">
                <!-- Fechar -->
                <span onclick="document.getElementById('divCadastro').style.display='none'" class="w3-button w3-display-topright">&times;</span>
                <h3>Cadastrar</h3>
            </header>
            <div class="w3-container w3-padding cadastro">
                <input type="text" class="w3-input w3-border w3-round w3-margin" placeholder="Nome" required pattern="[A-z\s]{6,100}" [(ngModel)]="obj.nome">
                <input type="email" class="w3-input w3-border w3-round w3-margin" placeholder="Email" required pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$" [(ngModel)]="obj.email">
                <input type="password" class="w3-input w3-border w3-round w3-margin" placeholder="Senha" required>
                <input type="password" class="w3-input w3-border w3-round w3-margin" placeholder="Confirmar senha" required>
                <input type="number" class="w3-input w3-border w3-round w3-margin" placeholder="CPF" required pattern="\d{3}\.\d{3}\.\d{3}-\d{2}">
                <input type="tel" class="w3-input w3-border w3-round w3-margin" placeholder="Telefone" required pattern="[0-9]{4,5}-[0-9]{4}">
                <div class="w3-padding">
                    <button class="w3-button w3-indigo w3-round w3-hover-orange" (click)="teste()">Cadastrar</button>
                </div>
            </div>
        </div>
    </div>
    

    <!-- Modal Esqueci a Senha -->
    <div class="w3-modal" id="divEsqueciSenha">
        <div class="w3-modal-content w3-card-4 w3-animate-top w3-round">
            <header class="w3-indigo w3-container modal-header">
                <span onclick="document.getElementById('divEsqueciSenha').style.display='none'" class="w3-button w3-display-topright">&times;</span>
                <h3>Esqueci minha senha</h3>
            </header>
            <form class="w3-container w3-padding cadastro">
                <p>Informe seu email para recuperar a senha</p>
                <input type="email" class="w3-input w3-border w3-round w3-margin" placeholder="Email" required pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$">
                <div class="w3-padding">
                    <button class="w3-button w3-indigo w3-round w3-hover-orange" (click)="recuperar()">Recuperar senha</button>

                </div>
            </form>
        </div>
    </div>
</section>
